    }
  }

  tableHeader(selection, e) {
    this.parent.formatter.process(this.parent, e, e.originalEvent, {
      selection: selection,
      subCommand: e.item.subCommand
    });
  }

  editAreaClickHandler(e) {
    if (this.parent.readonly || !isNullOrUndefined(closest(e.args.target, '.e-img-caption'))) {
      return;
    }

    const args = e.args;
    const showOnRightClick = this.parent.quickToolbarSettings.showOnRightClick;

    if (args.which === 2 || showOnRightClick && args.which === 1 || !showOnRightClick && args.which === 3) {
      return;
    }

    if (this.parent.editorMode === 'HTML' && this.parent.quickToolbarModule && this.parent.quickToolbarModule.tableQTBar) {
      this.quickToolObj = this.parent.quickToolbarModule;
      const target = args.target;
      this.contentModule = this.rendererFactory.getRenderer(RenderType.Content);
      const isPopupOpen = this.quickToolObj.tableQTBar.element.classList.contains('e-rte-pop');

      if (isPopupOpen) {
        return;
      }

      const range = this.parent.formatter.editorManager.nodeSelection.getRange(this.contentModule.getDocument());
      const closestTable = closest(target, 'table');
      const startNode = this.parent.getRange().startContainer.parentElement;
      const endNode = this.parent.getRange().endContainer.parentElement;

      if (target && target.nodeName !== 'A' && target.nodeName !== 'IMG' && startNode === endNode && (target.nodeName === 'TD' || target.nodeName === 'TH' || target.nodeName === 'TABLE' || closestTable && this.parent.contentModule.getEditPanel().contains(closestTable)) && !(range.startContainer.nodeType === 3 && !range.collapsed)) {
        const range = this.parent.formatter.editorManager.nodeSelection.getRange(this.contentModule.getDocument());
        this.parent.formatter.editorManager.nodeSelection.save(range, this.contentModule.getDocument());
        this.parent.formatter.editorManager.nodeSelection.Clear(this.contentModule.getDocument());
        const pageY = this.parent.iframeSettings.enable ? window.pageYOffset + this.parent.element.getBoundingClientRect().top + args.clientY : args.pageY;
        this.quickToolObj.tableQTBar.showPopup(args.pageX, pageY, target);
        this.parent.formatter.editorManager.nodeSelection.restore();
      } else {
        this.hideTableQuickToolbar();
      }
    }
  }

  tableCellSelect(e) {
    const target = e.target;
    const row = Array.prototype.slice.call(target.parentElement.parentElement.children).indexOf(target.parentElement);
    const col = Array.prototype.slice.call(target.parentElement.children).indexOf(target);
    const list = this.dlgDiv.querySelectorAll('.e-rte-tablecell');
    Array.prototype.forEach.call(list, item => {
      const parentIndex = Array.prototype.slice.call(item.parentElement.parentElement.children).indexOf(item.parentElement);
      const cellIndex = Array.prototype.slice.call(item.parentElement.children).indexOf(item);
      removeClass([item], 'e-active');

      if (parentIndex <= row && cellIndex <= col) {
        addClass([item], 'e-active');
      }
    });
    this.tblHeader.innerHTML = col + 1 + 'x' + (row + 1);
  } // eslint-disable-next-line


  tableMouseUp(e) {
    EventHandler.remove(this.curTable, 'mousemove', this.tableMove);
  } // eslint-disable-next-line


  tableCellLeave(e) {
    removeClass(this.dlgDiv.querySelectorAll('.e-rte-tablecell'), 'e-active');
    addClass([this.dlgDiv.querySelector('.e-rte-tablecell')], 'e-active');
    this.tblHeader.innerHTML = 1 + 'x' + 1;
  }

  tableCellClick(e) {
    const target = e.target;
    const row = Array.prototype.slice.call(target.parentElement.parentElement.children).indexOf(target.parentElement) + 1;
    const col = Array.prototype.slice.call(target.parentElement.children).indexOf(target) + 1;
    this.self.tableInsert(row, col, e, this);
  }

  tableInsert(row, col, e, selectionObj) {
    const proxy = selectionObj.self ? selectionObj.self : this;
    const startContainer = selectionObj.selection.range.startContainer;

    if (startContainer.nodeName === 'P' && startContainer.textContent.trim() === '' && !(startContainer.childNodes.length > 0)) {
      startContainer.innerHTML = '<br />';
    }

    const parentNode = startContainer.parentNode;

    if (proxy.parent.editorMode === 'HTML' && (proxy.parent.iframeSettings.enable && !hasClass(parentNode.ownerDocument.querySelector('body'), 'e-lib') || // eslint-disable-next-line
    !proxy.parent.iframeSettings.enable && isNullOrUndefined(closest(parentNode, '[id=' + "'" + proxy.contentModule.getPanel().id + "'" + ']')))) {
      proxy.contentModule.getEditPanel().focus();
      const range = proxy.parent.formatter.editorManager.nodeSelection.getRange(proxy.contentModule.getDocument());
      selectionObj.selection = proxy.parent.formatter.editorManager.nodeSelection.save(range, proxy.contentModule.getDocument());
    }

    const value = {
      rows: row,
      columns: col,
      width: {
        minWidth: proxy.parent.tableSettings.minWidth,
        maxWidth: proxy.parent.tableSettings.maxWidth,
        width: proxy.parent.tableSettings.width
      },
      selection: selectionObj.selection
    };

    if (proxy.popupObj) {
      proxy.popupObj.hide();
    }

    if (proxy.editdlgObj) {
      proxy.editdlgObj.hide();
    }

    proxy.parent.formatter.process(proxy.parent, selectionObj.args, selectionObj.args.originalEvent, value);
    proxy.contentModule.getEditPanel().focus();
    proxy.parent.on(mouseDown, proxy.cellSelect, proxy);
  }

  cellSelect(e) {
    let target = e.args.target;
    const tdNode = closest(target, 'td,th');
    target = target.nodeName !== 'TD' && tdNode && this.parent.contentModule.getEditPanel().contains(tdNode) ? tdNode : target;
    removeClass(this.contentModule.getEditPanel().querySelectorAll('table td, table th'), CLS_TABLE_SEL);

    if (target && (target.tagName === 'TD' || target.tagName === 'TH')) {
      addClass([target], CLS_TABLE_SEL);
      this.activeCell = target;
      this.curTable = this.curTable ? this.curTable : closest(target, 'table');
      EventHandler.add(this.curTable, 'mousemove', this.tableMove, this);
      EventHandler.add(this.curTable, 'mouseup', this.tableMouseUp, this);
      this.removeResizeElement();

      if (this.helper && this.contentModule.getEditPanel().contains(this.helper)) {
        detach(this.helper);
      }
    }

    if (Browser.info.name === 'mozilla' && !isNullOrUndefined(closest(target, 'table')) && closest(target, 'table').tagName === 'TABLE') {
      this.parent.contentModule.getEditPanel().setAttribute('contenteditable', 'false');
    }
  }

  tableMove(event) {
    this.parent.formatter.editorManager.observer.notify('TABLE_MOVE', {
      event: event,
      selectNode: [this.activeCell]
    });
  }

  resizeHelper(e) {
    if (this.parent.readonly) {
      return;
    }

    const target = e.target || e.targetTouches[0].target;
    const closestTable = closest(target, 'table');

    if (!isNullOrUndefined(this.curTable) && !isNullOrUndefined(closestTable) && closestTable !== this.curTable) {
      this.removeResizeElement();
      this.removeHelper(e);
      this.cancelResizeAction();
    }

    if (target.nodeName === 'TABLE' || target.nodeName === 'TD' || target.nodeName === 'TH') {
      this.curTable = closestTable && this.parent.contentModule.getEditPanel().contains(closestTable) && (target.nodeName === 'TD' || target.nodeName === 'TH') ? closestTable : target;
      this.removeResizeElement();
      this.tableResizeEleCreation(this.curTable, e);
    }
  }

  tableResizeEleCreation(table, e) {
    this.parent.preventDefaultResize(e);
    const columns = Array.prototype.slice.call(table.rows[this.calMaxCol(table)].cells, 1);
    const rows = [];

    for (let i = 0; i < table.rows.length; i++) {
      rows.push(Array.prototype.slice.call(table.rows[i].cells, 0, 1)[0]);
    }

    const height = parseInt(getComputedStyle(table).height, 10);
    const width = parseInt(getComputedStyle(table).width, 10);
    const pos = this.calcPos(table);

    for (let i = 0; columns.length > i; i++) {
      const colReEle = this.parent.createElement('span', {
        attrs: {
          'data-col': (i + 1).toString(),
          'unselectable': 'on',
          'contenteditable': 'false'
        }
      });
      colReEle.classList.add(CLS_RTE_TABLE_RESIZE, CLS_TB_COL_RES);
      colReEle.style.cssText = 'height: ' + height + 'px; width: 4px; top: ' + pos.top + 'px; left:' + (pos.left + this.calcPos(columns[i]).left) + 'px;';
      this.contentModule.getEditPanel().appendChild(colReEle);
    }

    for (let i = 0; rows.length > i; i++) {
      const rowReEle = this.parent.createElement('span', {
        attrs: {
          'data-row': i.toString(),
          'unselectable': 'on',
          'contenteditable': 'false'
        }
      });
      rowReEle.classList.add(CLS_RTE_TABLE_RESIZE, CLS_TB_ROW_RES);
      const rowPosLeft = !isNullOrUndefined(table.getAttribute('cellspacing')) || table.getAttribute('cellspacing') !== '' ? 0 : this.calcPos(rows[i]).left;
      rowReEle.style.cssText = 'width: ' + width + 'px; height: 4px; top: ' + (this.calcPos(rows[i]).top + pos.top + rows[i].offsetHeight - 2) + 'px; left:' + (rowPosLeft + pos.left) + 'px;';
      this.contentModule.getEditPanel().appendChild(rowReEle);
    }

    const tableReBox = this.parent.createElement('span', {
      className: CLS_TB_BOX_RES,
      attrs: {
        'data-col': columns.length.toString(),
        'unselectable': 'on',
        'contenteditable': 'false'
      }
    });
    tableReBox.style.cssText = 'top: ' + (pos.top + height - 4) + 'px; left:' + (pos.left + width - 4) + 'px;';

    if (Browser.isDevice) {
      tableReBox.classList.add('e-rmob');
    }

    this.contentModule.getEditPanel().appendChild(tableReBox);
  }

  removeResizeElement() {
    const item = this.parent.contentModule.getEditPanel().querySelectorAll('.e-column-resize, .e-row-resize, .e-table-box');

    if (item.length > 0) {
      for (let i = 0; i < item.length; i++) {
        detach(item[i]);
      }
    }
  }

  calcPos(elem) {
    let parentOffset = {
      top: 0,
      left: 0
    }; // eslint-disable-next-line

    const offset = elem.getBoundingClientRect();
    const doc = elem.ownerDocument;
    let offsetParent = elem.offsetParent || doc.documentElement;

    while (offsetParent && (offsetParent === doc.body || offsetParent === doc.documentElement) && offsetParent.style.position === 'static') {
      offsetParent = offsetParent.parentNode;
    }

    if (offsetParent.nodeName === 'TD' && elem.nodeName === 'TABLE') {
      offsetParent = closest(offsetParent, '.e-rte-content');
    }

    if (offsetParent && offsetParent !== elem && offsetParent.nodeType === 1) {
      // eslint-disable-next-line
      parentOffset = offsetParent.getBoundingClientRect();
    }

    return {
      top: elem.offsetTop,
      left: elem.offsetLeft
    };
  }

  getPointX(e) {
    if (e.touches && e.touches.length) {
      return e.touches[0].pageX;
    } else {
      return e.pageX;
    }
  }

  getPointY(e) {
    if (e.touches && e.touches.length) {
      return e.touches[0].pageY;
    } else {
      return e.pageY;
    }
  }

  resizeStart(e) {
    if (this.parent.readonly) {
      return;
    }

    if (Browser.isDevice) {
      this.resizeHelper(e);
    }

    const target = e.target;

    if (target.classList.contains(CLS_TB_COL_RES) || target.classList.contains(CLS_TB_ROW_RES) || target.classList.contains(CLS_TB_BOX_RES)) {
      e.preventDefault();
      this.parent.preventDefaultResize(e);
      removeClass(this.curTable.querySelectorAll('td,th'), CLS_TABLE_SEL);
      this.parent.formatter.editorManager.nodeSelection.Clear(this.contentModule.getDocument());
      this.pageX = this.getPointX(e);
      this.pageY = this.getPointY(e);
      this.resizeBtnInit();
      this.hideTableQuickToolbar();

      if (target.classList.contains(CLS_TB_COL_RES)) {
        this.resizeBtnStat.column = true;
        this.columnEle = this.curTable.rows[this.calMaxCol(this.curTable)].cells[parseInt(target.getAttribute('data-col'), 10)];
        this.colIndex = this.columnEle.cellIndex;
        this.moveEle = e.target;
        this.appendHelper();
      }

      if (target.classList.contains(CLS_TB_ROW_RES)) {
        this.rowEle = this.curTable.rows[parseInt(target.getAttribute('data-row'), 10)];
        this.resizeBtnStat.row = true;
        this.appendHelper();
      }

      if (target.classList.contains(CLS_TB_BOX_RES)) {
        this.resizeBtnStat.tableBox = true;
      }

      if (Browser.isDevice && this.helper && !this.helper.classList.contains('e-reicon')) {
        this.helper.classList.add('e-reicon');
        EventHandler.add(document, Browser.touchStartEvent, this.removeHelper, this);
        EventHandler.add(this.helper, Browser.touchStartEvent, this.resizeStart, this);
      } else {
        const args = {
          event: e,
          requestType: 'Table'
        };
        this.parent.trigger(resizeStart, args, resizeStartArgs => {
          if (resizeStartArgs.cancel) {
            this.cancelResizeAction();
          }
        });
      }

      EventHandler.add(this.contentModule.getDocument(), Browser.touchMoveEvent, this.resizing, this);
      EventHandler.add(this.contentModule.getDocument(), Browser.touchEndEvent, this.resizeEnd, this);
    }
  }

  removeHelper(e) {
    const cls = e.target.classList;

    if (!cls.contains('e-reicon') && this.helper) {
      EventHandler.remove(document, Browser.touchStartEvent, this.removeHelper);
      EventHandler.remove(this.helper, Browser.touchStartEvent, this.resizeStart);

      if (this.helper && this.contentModule.getEditPanel().contains(this.helper)) {
        detach(this.helper);
      }

      this.pageX = null;
      this.helper = null;
    }
  }

  appendHelper() {
    this.helper = this.parent.createElement('div', {
      className: 'e-table-rhelper'
    });

    if (Browser.isDevice) {
      this.helper.classList.add('e-reicon');
    }

    this.contentModule.getEditPanel().appendChild(this.helper);
    this.setHelperHeight();
  }

  setHelperHeight() {
    const pos = this.calcPos(this.curTable);

    if (this.resizeBtnStat.column) {
      this.helper.classList.add('e-column-helper');
      this.helper.style.cssText = 'height: ' + getComputedStyle(this.curTable).height + '; top: ' + pos.top + 'px; left:' + (pos.left + this.calcPos(this.columnEle).left - 1) + 'px;';
    } else {
      this.helper.classList.add('e-row-helper');
      this.helper.style.cssText = 'width: ' + getComputedStyle(this.curTable).width + '; top: ' + (this.calcPos(this.rowEle).top + pos.top + this.rowEle.offsetHeight) + 'px; left:' + (this.calcPos(this.rowEle).left + pos.left) + 'px;';
    }
  }

  updateHelper() {
    const pos = this.calcPos(this.curTable);

    if (this.resizeBtnStat.column) {
      const left = pos.left + this.calcPos(this.columnEle).left - 1;
      this.helper.style.left = left + 'px';
    } else {
      const top = this.calcPos(this.rowEle).top + pos.top + this.rowEle.offsetHeight;
      this.helper.style.top = top + 'px';
    }
  }

  calMaxCol(element) {
    let max = 0;
    let maxRowIndex;

    for (let i = 0; i < element.rows.length; i++) {
      if (max < element.rows[i].cells.length) {
        maxRowIndex = i;
        max = element.rows[i].cells.length;
      }
    }

    return maxRowIndex;
  }

  resizing(e) {
    const pageX = this.getPointX(e);
    const pageY = this.getPointY(e);
    const mouseX = this.parent.enableRtl ? -(pageX - this.pageX) : pageX - this.pageX;
    const mouseY = this.parent.enableRtl ? -(pageY - this.pageY) : pageY - this.pageY;
    this.pageX = pageX;
    this.pageY = pageY;
    const args = {
      event: e,
      requestType: 'table'
    };
    this.parent.trigger(onResize, args, resizingArgs => {
      if (resizingArgs.cancel) {
        this.cancelResizeAction();
      } else {
        const tableReBox = this.contentModule.getEditPanel().querySelector('.e-table-box');
        const tableWidth = parseInt(getComputedStyle(this.curTable).width, 10);
        const tableHeight = parseInt(getComputedStyle(this.curTable).height, 10);
        const paddingSize = +getComputedStyle(this.contentModule.getEditPanel()).paddingRight.match(/\d/g).join('');
        const rteWidth = this.contentModule.getEditPanel().offsetWidth - paddingSize * 2;

        if (this.resizeBtnStat.column) {
          const cellColl = this.curTable.rows[this.calMaxCol(this.curTable)].cells;
          const width = parseFloat(this.columnEle.offsetWidth.toString());
          const actualwid = width - mouseX;
          const totalwid = parseFloat(this.columnEle.offsetWidth.toString()) + parseFloat(cellColl[this.colIndex - 1].offsetWidth.toString());

          for (let i = 0; i < this.curTable.rows.length; i++) {
            if (totalwid - actualwid > 20 && actualwid > 20) {
              const leftColumnWidth = totalwid - actualwid;
              const rightColWidth = actualwid;

              if (!isNullOrUndefined(this.curTable.rows[i].cells[this.colIndex - 1])) {
                this.curTable.rows[i].cells[this.colIndex - 1].style.width = this.convertPixelToPercentage(leftColumnWidth, tableWidth) + '%';
              }

              if (!isNullOrUndefined(this.curTable.rows[i].cells[this.colIndex])) {
                this.curTable.rows[i].cells[this.colIndex].style.width = this.convertPixelToPercentage(rightColWidth, tableWidth) + '%';
              }
            }
          }

          this.updateHelper();
        } else if (this.resizeBtnStat.row) {
          this.parent.preventDefaultResize(e);
          const height = parseFloat(this.rowEle.clientHeight.toString()) + mouseY;

          if (height > 20) {
            this.rowEle.style.height = height + 'px';
          }

          this.curTable.style.height = '';

          if (!isNullOrUndefined(tableReBox)) {
            tableReBox.style.cssText = 'top: ' + (this.calcPos(this.curTable).top + tableHeight - 4) + 'px; left:' + (this.calcPos(this.curTable).left + tableWidth - 4) + 'px;';
          }

          this.updateHelper();
        } else if (this.resizeBtnStat.tableBox) {
          if (!Browser.isDevice) {
            EventHandler.remove(this.contentModule.getEditPanel(), 'mouseover', this.resizeHelper);
          }

          const widthType = this.curTable.style.width.indexOf('%') > -1;
          this.curTable.style.width = widthType ? this.convertPixelToPercentage(tableWidth + mouseX, rteWidth) + '%' : tableWidth + mouseX + 'px';
          this.curTable.style.height = tableHeight + mouseY + 'px';
          tableReBox.classList.add('e-rbox-select');
          tableReBox.style.cssText = 'top: ' + (this.calcPos(this.curTable).top + tableHeight - 4) + 'px; left:' + (this.calcPos(this.curTable).left + tableWidth - 4) + 'px;';
        }
      }
    });
  }

  convertPixelToPercentage(value, offsetValue) {
    return value / offsetValue * 100;
  }

  cancelResizeAction() {
    EventHandler.remove(this.contentModule.getDocument(), Browser.touchMoveEvent, this.resizing);
    EventHandler.remove(this.contentModule.getDocument(), Browser.touchEndEvent, this.resizeEnd);
    this.removeResizeElement();
  }

  resizeEnd(e) {
    this.resizeBtnInit();
    EventHandler.remove(this.contentModule.getDocument(), Browser.touchMoveEvent, this.resizing);
    EventHandler.remove(this.contentModule.getDocument(), Browser.touchEndEvent, this.resizeEnd);

    if (this.contentModule.getEditPanel().querySelector('.e-table-box') && this.contentModule.getEditPanel().contains(this.contentModule.getEditPanel().querySelector('.e-table-box'))) {
      if (!Browser.isDevice) {
        EventHandler.add(this.contentModule.getEditPanel(), 'mouseover', this.resizeHelper, this);
      }

      this.removeResizeElement();

      if (this.helper && this.contentModule.getEditPanel().contains(this.helper)) {
        detach(this.helper);
        this.helper = null;
      }

      this.pageX = null;
      this.pageY = null;
      this.moveEle = null;
    }

    const args = {
      event: e,
      requestType: 'table'
    };
    this.parent.trigger(resizeStop, args);
    this.parent.formatter.saveData();
  }

  resizeBtnInit() {
    return this.resizeBtnStat = {
      column: false,
      row: false,
      tableBox: false
    };
  }

  addRow(selectCell, e, tabkey) {
    let cmd;

    if (tabkey) {
      cmd = {
        item: {
          command: 'Table',
          subCommand: 'InsertRowAfter'
        }
      };
    }

    const value = {
      selection: selectCell,
      subCommand: tabkey ? cmd.item.subCommand : e.item.subCommand
    };
    this.parent.formatter.process(this.parent, tabkey ? cmd : e, e, value);
  }

  addColumn(selectCell, e) {
    this.parent.formatter.process(this.parent, e, e, {
      selection: selectCell,
      width: this.parent.tableSettings.width,
      subCommand: e.item.subCommand
    });
  }

  removeRowColumn(selectCell, e) {
    this.parent.formatter.process(this.parent, e, e, {
      selection: selectCell,
      subCommand: e.item.subCommand
    });
    this.hideTableQuickToolbar();
  }

  removeTable(selection, args, delKey) {
    let cmd;

    if (delKey) {
      cmd = {
        item: {
          command: 'Table',
          subCommand: 'TableRemove'
        }
      };
    }

    const value = {
      selection: selection,
      subCommand: delKey ? cmd.item.subCommand : args.item.subCommand
    };
    this.parent.formatter.process(this.parent, delKey ? cmd : args, args.originalEvent, value);
    this.contentModule.getEditPanel().focus();
    this.removeResizeElement();
    this.hideTableQuickToolbar();
  }

  renderDlgContent(args) {
    if (Browser.isDevice || this.parent.inlineMode.enable) {
      this.insertTableDialog(args);
      return;
    }

    if (this.popupObj) {
      this.popupObj.hide();
      return;
    }

    this.hideTableQuickToolbar();
    const header = '1X1';
    const insertbtn = this.l10n.getConstant('inserttablebtn');
    this.dlgDiv = this.parent.createElement('div', {
      className: 'e-rte-table-popup',
      id: this.rteID + '_table'
    });
    this.tblHeader = this.parent.createElement('div', {
      className: 'e-rte-popup-header'
    });
    this.tblHeader.innerHTML = header;
    this.dlgDiv.appendChild(this.tblHeader);
    const tableDiv = this.parent.createElement('div', {
      className: 'e-rte-table-span'
    });
    this.drawTable(tableDiv, args);
    this.dlgDiv.appendChild(tableDiv);
    this.dlgDiv.appendChild(this.parent.createElement('span', {
      className: 'e-span-border'
    }));
    const btnEle = this.parent.createElement('button', {
      className: 'e-insert-table-btn',
      id: this.rteID + '_insertTable',
      attrs: {
        type: 'button',
        tabindex: '0'
      }
    });

    if (!isNullOrUndefined(this.parent.getToolbarElement().querySelector('.e-expended-nav'))) {
      this.parent.getToolbarElement().querySelector('.e-expended-nav').setAttribute('tabindex', '1');
    }

    this.dlgDiv.appendChild(btnEle);
    const button = new Button({
      iconCss: 'e-icons e-create-table',
      content: insertbtn,
      cssClass: 'e-flat',
      enableRtl: this.parent.enableRtl,
      locale: this.parent.locale
    });
    button.isStringTemplate = true;
    button.appendTo(btnEle);
    EventHandler.add(btnEle, 'click', this.insertTableDialog, {
      self: this,
      args: args.args,
      selection: args.selection
    });
    this.parent.getToolbar().appendChild(this.dlgDiv);
    let target = args.args.originalEvent.target;
    target = target.classList.contains('e-toolbar-item') ? target.firstChild : target.parentElement;
    this.popupObj = new Popup(this.dlgDiv, {
      targetType: 'relative',
      relateTo: target,
